import { Component, OnInit, Sanitizer } from '@angular/core';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';
import { UserService } from '../service/user.service';
import { LoginService } from '../service/login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})

export class HeaderComponent implements OnInit {
  imageSrc:SafeUrl='';
  status!:String;
  userName:String = "";
  isVisible:boolean =false;

  constructor(private userService:UserService,private sanitizer:DomSanitizer,private logSer:LoginService,private router:Router){}
  ngOnInit(): void {
    this.status="login";
    this.logSer.userLoggedIn.subscribe((email:boolean)=>{      
      if(email==true){
      this.status="logout";
      }
    });
    this.userService.getProfileImg().subscribe((data:any)=>{
      if(data && data.imageData){
        const imageData = data.imageData;
        const binaryData = atob(imageData);
        const arrayBuffer = new ArrayBuffer(binaryData.length);
        const uint8Array = new Uint8Array(arrayBuffer);
        for (let i = 0; i < binaryData.length; i++) {
          uint8Array[i] = binaryData.charCodeAt(i);
        }
        const blob = new Blob([uint8Array], { type: 'image/png' });
        this.imageSrc = this.sanitizer.bypassSecurityTrustUrl(URL.createObjectURL(blob));
      }
    })

    this.userService.getUserName().subscribe((data:any)=>{
    this.userName = data.name;

    })
  } 
  clicked(){
    if(this.status==="logout"){
      localStorage.clear();
      alert("cleared");
      this.logSer.onFailure();
      this.router.navigateByUrl("/login")
      this.status="login"
        }
  }
  toCart(){
    this.router.navigateByUrl("/cart");
  }
  clickedList(){
    if(!this.isVisible){
      this.isVisible=true;
    }
    else{
      this.isVisible=false;
    }

  }

}
