import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent {

  constructor(private route:Router){}

  role2:any = localStorage.getItem("role");

  add(){

    this.route.navigateByUrl('/adminrestaurantView');
  }

  addFoodItem(){
    this.route.navigateByUrl('/adminFoodItemView');
  }

}

<div class="background" style="position: static;">
    <!-- <h1 style="text-align: center;"> Admin view</h1> -->
<div class="shape">
    <button mat-raised-button color="primary" (click)="add()" >Add or edit restaurant</button>
    <hr>
   <button style="margin-left: 170px;" mat-raised-button color="accent" (click)="addFoodItem()">Add or edit new Food item</button>
</div>
</div>
