import { Component } from '@angular/core';
import { RestaurantServiceService } from '../service/restaurant-service.service';
import { ActivatedRoute, Router } from '@angular/router';
import { LoginService } from '../service/login.service';
import { Order } from '../model/order';
import { Item } from '../model/item';
import { FoodieService } from '../service/foodie.service';

@Component({
  selector: 'app-view-items',
  templateUrl: './view-items.component.html',
  styleUrls: ['./view-items.component.css']
})
export class ViewItemsComponent {
  constructor(private restaurant:RestaurantServiceService,private route:Router,private login:LoginService,private foodie:FoodieService,private user){}
  addtocart!:Order;
  data:number | undefined;
  restaurantItems:any=[];
  restaurantId!: number;

  ngOnInit(): void {
    this.data = 51;
    this.restaurantId = this.restaurant.id;
    this.restaurant.getAllItems(+this.restaurantId).subscribe(data=>{      
      this.restaurantItems = data;
    })
  }
// getItems() {
