import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from '../service/user.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { LoginService } from '../service/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  responseData:any;

  constructor(private fb:FormBuilder,private userService:UserService,private _sanckBar:MatSnackBar,private route:Router,private loginStatus:LoginService){}

  loginForm = this.fb.group({
    email:["",[Validators.required,this.checkIfGuestEmailsAreValid]],
    password:["",[Validators.required,Validators.minLength(7)]]
  })

  get email(){
    return this.loginForm.get('email');
  }

  get password(){
    return this.loginForm.get('password')
  }

  checkIfGuestEmailsAreValid(c: AbstractControl) {

    if (c.value !== '') {
      const emailString = c.value;
      const emails = emailString.split(',').map((e: string) => e.trim());
      const emailRegex = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
      const anyInvalidEmail = emails.every((e: string) => e.match(emailRegex) !== null);
      if (!anyInvalidEmail) {
        return { checkIfGuestEmailsAreValid: false }
      }
    }
    return null;
  }

  loginCheck(){
    // console.log(this.loginForm);

      this.userService.login(this.loginForm.value).subscribe({
        next:data=>{
          this.responseData=data;
          console.log(this.responseData);

          localStorage.setItem('Token',this.responseData.Token);
          localStorage.setItem('email',this.responseData.email);

          this._sanckBar.open('Logged In successfully.....', 'success', {
            duration: 3000,
            panelClass: ['mat-toolbar', 'mat-primary']
          });
          this.loginStatus.loginSuccess();
          this.ro
        },error:(err)=>{
           this.loginStatus.onFailure()
          alert("Authorization failed, Please check the provided details again!")
        }
      })
  }
}
<div class="loginPage">
    <section>
        <div id="imgBx">
         <img src="../../assets/loginImg.png" alt="">
        </div>
        <div id="formLogin">
            <!-- <img id="svgImg" src="../../assets/graph.png" alt=""> -->
            <div id="formBx">
            <h2>Login to Continue..!</h2>
                <form (ngSubmit)="loginCheck()" [formGroup]="loginForm">

                    <div id="emailInP">
                        <span>Email &nbsp;&nbsp;</span>
                        <input type="text" placeholder="Enter your Email" formControlName="email">
                        <div id="err" *ngIf="email?.errors?.['required'] && email?.dirty">
                            Email is required
                        </div>
                        <div id="err" *ngIf="email?.errors?.['checkIfGuestEmailsAreValid']===false && email?.dirty">
                           Not a valid email 
                        </div>
                    </div>

                    <div id="passwordInp">
                        <span>Password</span>
                        <input type="password" placeholder="Enter your Password" formControlName="password">
                        <div id="err" *ngIf="password?.errors?.['required'] && password?.dirty">
                            Password is required
                        </div>
                        <div id="err" *ngIf="password?.errors?.['minlength']">
                            Password must be {{password?.errors?.['minlength'].requiredLength}} characters long
                        </div>
                    </div>

                    <br>
                    <button mat-raised-button type="submit" [disabled]="!loginForm.valid">Sign In</button>

                </form>

              <p>Don't have an account?<a routerLink="/registration">Sign up</a></p>
            </div>
        </div>
    </section>
</div>
