<h1 style="text-align: center;">Have a delicious food at available restaurants !!</h1>

                             <!-- for Items -->

<div *ngIf="present == true" class="background">
<mat-card class="example-card" *ngFor="let item of searchedItems">
    <img mat-card-image src="{{item.imageUrl}}" width="400px" height="250px">
    <h2
        style="text-align: center; text-transform: uppercase; font-family:'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;color: rgb(0, 0, 0);font-weight: bolder;">
        <span> {{item.itemName}}</span></h2>
    <mat-card-content>
        <h3>
            <div style="text-align: left;">Price: {{item.itemPrice}}</div>
            <a style="color: black;  " target="_blank" rel="noopener">
                <div style="text-align: center;">
                    <div class="github-star-badge">Rating: {{item.itemRating}}
                        <svg class="material-icons" xmlns="http://www.w3.org/2000/svg" style="background-color: rgb(255, 255, 255);" width="24" height="24" viewBox="0 0 24 24">
                            <path d="M0 0h24v24H0z" fill="none" />
                            <path d="M12 17.27L18.18 21l-1.64-7.03L22 9.24l-7.19-.61L12 2 9.19 8.63 2 9.24l5.46 4.73L5.82 21z" />
                        </svg>
                    </div>
                </div>
            </a>
        </h3>
    </mat-card-content>
    <mat-card-actions style="margin-left: 130px;">
        <button mat-raised-button color="primary"(click)="addToCart(item)">Add to cart</button>
      </mat-card-actions> 
</mat-card>
</div>

                                            <!-- for restraunts -->

<div *ngIf="present == true" class="background">
<mat-card class="example-card" *ngFor="let restaurant of searchedRestaurants" (click)="getItems(restaurant.restaurantId)">
